class Trans_Tag extends H2o_Node {
    public $pagina, $label, $lang = 'it';    

    function __construct($argstring, $parser, $position = 0) {
        $arguments = array_map('trim', explode(',', $argstring));
        $conta = 0;
        foreach($arguments as $argument) {
            switch ($conta){
                case 0:
                    $this->pagina = trim($argument);
                break;
                case 1:
                    $this->label = trim($argument);
                break;
                case 2:
                     $this->lang = trim($argument);
                break;
            }
            $conta += 1;
        }
    }

    function render($context, $stream) {
        $stream->write($this->getMessage($this->pagina,$this->label,$context["locale"]));
    }

    function getMessage($pagina, $label, $lang) {
        global $messages;
        return $messages[$pagina][$label][$lang];
    }

}


H2o::addTag('trans');
